import React, {useEffect, useState} from 'react';
import styles from "./flashSuccess.module.css";

export default function FlashSuccess({show, onClose}) {
  const [showFlash, setShowFlash] = useState(show);

  useEffect(() => {
    setTimeout(() => {
      onClose()
    }, 6000)
  }, [showFlash]);

  return (
    showFlash ? (
    <div className={styles.flashSuccess}>
      <h2 className={styles.title}>
        Success
      </h2>
      <p>
        message goes into here
