const contractDefined_JS = new web3.eth.Contract(
  contractABI_JS,
  contractAddress_JS
);


const colors =  [
  "RED",
  "BLUE",
  "YELLOW",
  "GREEN",
  "PURPLE",
  "ORANGE",
  "PINK",
  "WHITE",
];

let ArrayStorage = [];

function timeout(ms) {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

async function updateLights() {
  while (true) {
    if (ArrayStorage.length == 0) {
      for (let randomNumbers = 0; randomNumbers < 2; randomNumbers++) {
        console.log("API CALL");
        contractDefined_JS.methods
          .twoRandomWords(randomNumbers)
          .call((err, balance) => {
            ArrayStorage.push(balance % 255);
          });
        await timeout(timeMilliSec);
      }
    }
    if (ArrayStorage.length > 0) {
      for (let randomNumbers = 0; randomNumbers < 2; randomNumbers++) {
        for (let i = 0; i < 2; i++) {
          console.log(ArrayStorage[i]);
          for (let ledValue = 0; ledValue < 8; ledValue++) {
            if ((ArrayStorage[i] & (2 ** ledValue)) == 2 ** ledValue) {
              console.log(
                "COLOR " +
                  objectLED["color"][ledValue] +
                  " PIN " +
                  objectLED["pin"][ledValue] +
                  " ON!"
              );
            } else {
              console.log(
                "COLOR " +
                  objectLED["color"][ledValue] +
                  " PIN " +
                  objectLED["pin"][ledValue] +
                  " OFF!"
              );
            }
          }
          await timeout(timeMilliSec);
        }
      }
    }
  }
}

console.log("Contract starting value:");
updateLights();

contractDefined_JS.events
  .lightShowUpdate(
    {
      //Subscribe to event.
      fromBlock: "latest",
    },
    function (error, eventResult) {}
  )
  .on("data", function (eventResult) {
    console.log("EVENT DETECTED! NEW STATE VALUE: ");
    ArrayStorage = []; //WIPE LAST VALUES THEN UPDATE AGAIN.
    updateLights(); //Call the get function to get the most accurate present state for the value.
  })
  .on("changed", function (eventResult) {
    // remove event from local database
  })
  .on("error", console.error);


const vrf = () => {
  return (
    <div class="container">
    <div class="row">
      <div class="col-5">
        <h1><br></br>
          <b>About</b>
        </h1>
        <p>Submission for the ETH Denver Virtual Hackathon #BUIDL event</p>
          <br></br>
       </div>
      <div class="col-6">
        <img src={EthLogo} style={{ width: "40%" }} />
      </div>
      <div class="col"></div>
    </div>
  </div>
  );
};

