import { Modal } from "react-bootstrap";
import React, { Component, useEffect, useState } from "react";
import "./ConnectWalletModal.css";

export default function ErrorModal({ showToastFromProp, errorMsg, onClose }) {
  const [showModal, setShowModal] = useState(false);

  const _closeToast = () => {
    setShowModal(false);
    onClose();
  };

  const _openToast = () => {};

  useEffect(() => {
    const updateStateIfPropChanges = async () => {
      if (showToastFromProp) {
        setShowModal(true);
      } else if (!showToastFromProp) {
        setShowModal(false);
      }
    };
    updateStateIfPropChanges();
  }, [showToastFromProp]);

  const renderConnectWalletModal = () => {
    console.log(showModal, "showtoast t or f?");
    return (
        <Modal
        show={true}
        onHide={_closeToast}
    >
        <Modal.Header closeButton>
            <Modal.Title>{i18n.t('common_sign_out')}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <div className="col-md-12">{i18n.t('navbar_confirm_logout')}</div>
        </Modal.Body>
        <Modal.Footer>
            <button className="btn btn-danger" onClick={this._logout}>
                {i18n.t('common_sign_out')}
            </button>
            <button
                className="btn btn-primary"
                onClick={this._closeConfirmLogoutModal}
            >
                {i18n.t('navbar_cancel_logout')}
            </button>
        </Modal.Footer>
    </Modal>
    );
  };
  return <div> {renderConnectWalletModal()}</div>;
}
