import { Modal } from "react-bootstrap";
import React, { Component, useEffect, useState } from "react";
import "./ConnectWalletModal.css";
import { Web3ReactProvider, useWeb3React } from "@web3-react/core";
import { InjectedConnector } from "@web3-react/injected-connector";
import { Web3Provider } from "@ethersproject/providers";
import {
  injected,
  walletconnect,
  resetWalletConnector,
  walletlink,
} from "./connectors";

export default function ConnectWalletModal({
  showToastFromProp,
  errorMsg,
  onClose,
}) {
  const [showModal, setShowModal] = useState();
  const { active, account, library, connector, activate, deactivate } =
    useWeb3React();
  const web3 = useWeb3React();
  const shortAddress = account
    ? String(account).substr(0, 5) + "..." + String(account).substr(38, 4)
    : "";

  const _closeModal = () => {
    setShowModal(false);
    onClose();
  };

  const handleConnect = () => {
    try {
      web3.activate(injected, undefined, true);
    } catch (error) {
      console.error(error);
    }
  };

  const connectCoinbaseSimple = async () => {
    try {
      await web3.activate(walletlink);
    } catch (ex) {
      console.log(ex);
    }
  };

  useEffect(() => {
    const updateStateIfPropChanges = async () => {
      if (showToastFromProp) {
        setShowModal(true);
      } else if (!showToastFromProp) {
        setShowModal(false);
      }
    };
    updateStateIfPropChanges();
  }, [showToastFromProp]);

  const renderConnectWalletModal = () => {
    console.log(showModal, "showtoast t or f?");
    return (
      <Modal show={showToastFromProp} onHide={_closeModal}>
        <div style={{ margin: "10px" }}>
          <Modal.Title>
            <p>Connect a wallet</p>
          </Modal.Title>
        </div>
        <Modal.Body>
          <div className="col-md-12">{account ? shortAddress : ""}</div>
          <div>
            <div style={{ width: "500%" }}>
              <button class="btn-hover color-electric" onClick={handleConnect}>
                Connect Metamask
              </button>
              <pre></pre>
              <div>
                <button
                  class="btn-hover color-electric"
                  onClick={connectCoinbaseSimple}
                >
                  Connect Coinbase Wallet
                </button>
                <pre></pre>
              </div>
              <div>
                <button
                  class="btn-hover color-electric"
                  onClick={connectCoinbaseSimple}
                >
                  Connect XX Wallet
                </button>
                <pre></pre>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <button className="btn btn-danger" onClick="">
            Btn
          </button>
          <button className="btn btn-primary" onClick="">
            Btn
          </button>
        </Modal.Footer>
      </Modal>
    );
  };
  return <div> {renderConnectWalletModal()}</div>;
}
