import React, { Component, useEffect, useState } from "react";
import "./ConnectWalletModal.css";

export default function ErrorModal({ showToastFromProp, errorMsg, onClose }) {
  const [showModal, setShowModal] = useState(false);

  const _closeToast = () => {
    setShowModal(false);
    onClose();
  };

  const _openToast = () => {};

  useEffect(() => {
    const updateStateIfPropChanges = async () => {
      if (showToastFromProp) {
        setShowModal(true);
      } else if (!showToastFromProp) {
        setShowModal(false);
      }
    };
    updateStateIfPropChanges();
  }, [showToastFromProp]);

  const renderConnectWalletModal = () => {
    console.log(showModal, "showtoast t or f?");
    return (
        <Modal
        show={true}
        onHide={_closeToast}
