        </Modal.Header>
        <Modal.Body>
            <div className="col-md-12">Hello</div>
        </Modal.Body>
        <Modal.Footer>
            <button className="btn btn-danger" onClick="">
                Btn	<Modal
				show={this.state.confirmLogoutModalIsOpen}
				onHide={this._closeConfirmLogoutModal}
			>
				<Modal.Header closeButton>
					<Modal.Title>Titel</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<div className="col-md-12">{i18n.t('navbar_confirm_logout')}</div>
				</Modal.Body>
				<Modal.Footer>
					<button className="btn btn-danger" onClick={this._logout}>
						{i18n.t('common_sign_out')}
					</button>
					<button
						className="btn btn-primary"
						onClick={this._closeConfirmLogoutModal}
					>
						{i18n.t('navbar_cancel_logout')}
					</button>
				</Modal.Footer>
			</Modal>
            </button>
            <button
                className="btn btn-primary"
                onClick=""
            >
                Btn
            </button>
        </Modal.Footer>
    </Modal>
    );
  };
  return <div> {renderConnectWalletModal()}</div>;
}
