import { Modal } from "react-bootstrap";
import React, { Component, useEffect, useState } from "react";

export default function ErrorModal({ degree, userLocation, basic }) {
    const [showToast, setShowToast] = useState(false);

	const _renderInviteToast = () => {
		return (
			<Modal
				show={this.state.showToast}
				onHide={this._closeToast}
				aria-labelledby="contained-modal-title-vcenter"
				centered
			>
				<Modal.Header>Warning</Modal.Header>
				<Modal.Body>
					<div className="inv-toast-body">
						You have to connect to Metamask before doing this!
					</div>
				</Modal.Body>
			</Modal>
		);
	}
  return (
    <div> {_renderInviteToast()}</div>
  );
}
