import EthLogo from "../eth_logo.svg";
import React, { Component, useEffect, useState } from "react";

export default function Wrap({ degree, userLocation, basic }) {
  const [apiData, setApiData] = useState([]);
  const [userWethInput, setUserWethInput] = useState(0);
  const [userEthInput, setUserEthInput] = useState(0);

  /*   useEffect(() => {
    const fetchApiData = async () => {
      const liveUrl =
        "https://allemansbacken.newseed.se/v1/gcd?latitude=4&longitude=5"
      const res = await fetch(liveUrl);
      const data = await res.json();
      setApiData(data);
    };
    fetchApiData();
  }, []); */

  const renderInputBox = () => {
    return (
      <>
        <div style={{ marginBottom: 150, marginTop: 50 }}>
          <input
            type="number"
            class="form-control"
            placeholder="Enter ETH amount"
            data-name="eth"
            value={userEthInput}
            style={{ width: "50%", float: "left" }}
          ></input>
          <button type="button" class="btn btn-light" style={{ float: "left" }}>
            Wrap
          </button>
        </div>
        <input
          type="number"
          class="form-control"
          placeholder="Enter WETH amount"
          value={userWethInput}
          data-name="weth"
          onChange={(e) => handleChange(e)}
          style={{ width: "50%", float: "left" }}
        ></input>
        <button type="button" class="btn btn-light" style={{ float: "left" }}>
          Unwrap
        </button>
      </>
    );
  };

  const handleChange = (event) => {
    let value = event.target.value;
    let name = event.target.dataset.name;
    if (name == "eth") {
      setUserEthInput(value);
      console.log(userEthInput, "userEthInput");
    } else {
      setUserWethInput(value);
      console.log(userWethInput, "userWethInput");
    }
  };

  return (
    <div class="container">
      <div class="row">
        <div class="col-5">
          <h1>
            <br></br>
            <b>Wrap here </b>
          </h1>

          {renderInputBox()}
        </div>
        <div class="col-6">
          <img src={EthLogo} style={{ width: "40%" }} />
        </div>
        <div class="col"></div>
      </div>
    </div>
  );
}
