import { NavLink } from "react-router-dom";
import { faBolt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {DataContext} from '../DataContext';
import React, { Component, useEffect, useState } from "react";



import classes from "./Navbar.module.css";

const Navbar = ({handleConnectMetamask, connectedAddrValue}) => {
  const {userAccountAddress, setUserAccountAddress} = React.useContext(DataContext);

  return (
    <header className={classes.header}>


      <nav class="navbar">


        <div style={{ fontSize: "xx-large", marginRight: "50px" }}>
          <div class="h-screen flex justify-center items-center bg-gray-800">
            <h1 class="gradient-text font-sans font-black text-6xl">
              <FontAwesomeIcon icon={faBolt} /> ElectricKeeper
            </h1>
          </div>
        </div>
        <ul id="ul-navigation" class="nav-ul" data-visible="false">
          <li class="nav-li">
            <NavLink
              className={(navObj) => (navObj.isActive ? classes.active : "")}
              to="/"
            >
              Home
            </NavLink>
          </li>
          <li class="nav-li">
            <NavLink
              className={(navObj) => (navObj.isActive ? classes.active : "")}
              to="/buy"
            >
              Buy
            </NavLink>{" "}
          </li>
          <li class="nav-li">
            <NavLink
              className={(navObj) => (navObj.isActive ? classes.active : "")}
              to="/about"
            >
              About
            </NavLink>{" "}
          </li>
          <li class="nav-li">
          <a
              className={(navObj) => (navObj.isActive ? classes.active : "")}

            >
              Halloja<button
                className="hallo"
                onClick={handleConnectMetamask}
              >
                <img
                  width="30"
                  height="30"
                  style={{ marginRight: 5 }}
                  src="https://cdn.discordapp.com/attachments/908513230714982410/913132016365633596/aaaaa.png"
                ></img>
                {connectedAddrValue !== ""
                  ? connectedAddrValue
                  : "Connect Metamask"}
              </button>
            </a>{" "}          
          </li>
        </ul>
        <button
          class="mobile-toggle"
          aria-controls="ul-navigation"
          aria-expanded="false"
        >
          <span class="sr-only">Menu</span>
        </button>
      </nav>
    </header>
  );
};

export default Navbar;
