import React, { Component, useEffect, useState } from "react";
import Web3 from "web3";

import ErrorModal from "../components/ErrorModal";
import { Web3ReactProvider, useWeb3React } from "@web3-react/core";

import { DataContext } from "../DataContext";

const DOMINO_CONTRACT_ADDRESS = "0x3ba893c2d184411b6776d5830865e895af264f5a";
const DOMINO_CONTRACT_ABI = [
  {
    inputs: [
      {
        internalType: "contractElectricEthereum",
        name: "_electricEthereum",
        type: "address",
      },
    ],
    stateMutability: "nonpayable",
    type: "constructor",
  },
  {
    inputs: [],
    name: "BuyAllSameDuration",
    outputs: [],
    stateMutability: "payable",
    type: "function",
  },
  {
    inputs: [],
    name: "BuyAllTurnOffSlowly",
    outputs: [],
    stateMutability: "payable",
    type: "function",
  },
];

//TODO: add ErrorModal
//MetaMask wallet shown/button if connect
//Dropdown for network switch statements

export default function Owner({ degree, userLocation, basic }) {
  const [maticPriceFeedContract, setMaticPriceFeedContract] = useState(null);
  const [electricKeeperContract, setElectricKeeperContract] = useState(null);

  const [inputAmount, setInputAmount] = useState("");
  const [latestPriceOfMatic_1p, setLatestPriceOfMatic_1p] = useState("");
  const [showToast, setShowToast] = useState();
  const [errorMsg, setErrorMsg] = useState("");
  const { userAccountAddress, setUserAccountAddress } =
    React.useContext(DataContext);

  const { account } = useWeb3React();

  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on("chainChanged", () => {
        window.location.reload();
      });
      window.ethereum.on("accountsChanged", () => {
        window.location.reload();
      });
    }
    const loadBlockchainData = async () => {
      const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");
      //const network = await web3.eth.net.getNetworkType();
      //await window.ethereum.enable();
      //const addressFromMetamask = await web3.eth.getAccounts();
      const chainId = await web3.eth.getChainId();
      console.log(chainId);
      if (chainId !== 80001) {
        setErrorMsg("Must be on the Mumbai test network");
      }

      //Load the smart contract
      const dominoContract = new web3.eth.Contract(
        DOMINO_CONTRACT_ABI,
        DOMINO_CONTRACT_ADDRESS
      );
      setElectricKeeperContract(dominoContract);

      console.log(dominoContract, "This is DOMINO contract");
    };
    loadBlockchainData();
  }, [account]);

  useEffect(() => {
    console.log("matic Price Feed contract: ", maticPriceFeedContract);
  }, []);

  const estimatedMatic = () => {
    return latestPriceOfMatic_1p && inputAmount !== ""
      ? (latestPriceOfMatic_1p * (10 * inputAmount)).toFixed(3).toString()
      : "0";
  };

  const handleBuyButtonClick = (colorNumber) => {
    console.log("You chose the color:", colorNumber);
    console.log(account, "account in BUY handle click");
    try {
      let web3 = new Web3(window.web3.currentProvider);
      let amountOfMaticToPay = estimatedMatic();
      console.log(amountOfMaticToPay);
      web3.eth.sendTransaction({
        to: ELECTRICKEEPER_CONTRACT_ADDRESS,
        data: electricKeeperContract.methods
          .BuyElectricityTimeOn(
            colorNumber,
            web3.utils.toWei(amountOfMaticToPay)
          )
          .encodeABI(),
        value: web3.utils.toWei(amountOfMaticToPay),
        from: account,
      });
    } catch (err) {
      const msg = "Connect your wallet to buy";
      console.log(err, msg);
      setErrorMsg(msg);
    }
  };

  const renderButton = () => {
    return (
      <>
        <div
          style={{
            marginBottom: 150,
            marginTop: 50,
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
          }}
        >
          <button
            style={{ width: 400 }}
            className="btn-hover color-electric"
            onClick={() => handleBuyButtonClick}
          >
            request domino demo
          </button>
        </div>
        <br />
      </>
    );
  };

  return (
    <div class="container">
      <div class="row">
        <div>
          <h1>
            <br></br>
            owner
          </h1>
          <p>request 8 minute LED countdown</p>
          <div className="row">{renderButton()}</div>
        </div>
      </div>
      {errorMsg !== "" ? (
        <ErrorModal
          showToastFromProp={errorMsg !== ""}
          onClose={() => setErrorMsg("")}
          errorMsg={errorMsg}
        ></ErrorModal>
      ) : null}
    </div>
  );
}
